bool pointInPoly(Vec2d point, const vector<Vec2d>& polygon){
    int counter = 0;
    double x = 0;
    int j;
    for(unsigned int i = 0; i < polygon.size(); i++){
        if(i == polygon.size() - 1){
            j = 0;
        }
        else{
            j = i+1;
        }
        Vec2d p1 = polygon[i];
        Vec2d p2 = polygon[j];

        if(((p1.y < point.y) && (point.y <= p2.y)) || ((p1.y >= point.y) && (point.y > p2.y))){
            double slope = (p2.y-p1.y)/(p2.x-p1.x);
            x = ((point.y-p2.y)/slope) + p2.x;
            if(x > point.x){
                counter++;
            }
        }
    }
    if(counter % 2){
        return true;
    }
    else{
        return false;
    }
}


